var lat="",lon="";
var api="https://fcc-weather-api.glitch.me/api/current?";
var images={
    "Clear":"data:image/jpeg;base64,",
    "Mist": "https://s-media-cache-ak0.pinimg.com/originals/a9/61/b9/a961b91df2ec59eb7c48f7fd762e16ad.jpg",
    "Clouds":"https://andersbjornsbo.files.wordpress.com/2011/12/treeinclouds.jpg",
    "Thunderstom":"https://www.earthnetworks.com/wp-content/uploads/2017/01/iStock-186538582-ColumnA-min.jpg",
    "Snow":"data:image/jpeg;base64,",
    "Rain":"https://cmeimg-a.akamaihd.net/640/photos.demandstudios.com/getty/article/161/235/80406500.jpg",
    "Drizzle":"https://metrouk2.files.wordpress.com/2014/10/1400x95222.jpg?w=748&h=508&crop=1",
    "Haze":"data:image/jpeg;base64,"
};
$(document).ready(
	function()
		{
			if (navigator.geolocation)
			{
    			navigator.geolocation.getCurrentPosition(
    				function(position)
    				{
    					lat="lat="+position.coords.latitude;
    					lon="lon="+position.coords.longitude;
      					getWeather(lat,lon);
      				}
				);
    				
    		}

            $("#unit").click(
                function()
                {
                    var currunit=$("#unit").text(), currtemp=parseInt($("#temp").text()),newunit,newtemp;
                    if (currunit=='C')
                    {
                        newunit='F';
                        newtemp=Math.round((currtemp*1.8)+32);
                    }
                    else
                    {
                        newunit='C';
                        newtemp=Math.round((currtemp-32)/1.8);
                    }

                    $("#temp").text(newtemp.toString()+String.fromCharCode(176));
                    $("#unit").text(newunit);
                }
            );
		}
	);

function getWeather()
{
    var fullpath=api+lat+"&"+lon;
	$.ajax(
    	{
    		url:fullpath,
    		success:function(data)
    				{
    					$("#city").text(data.name+",");
    					$("#country").text(data.sys.country);
    					$("#temp").text(Math.round(data.main.temp)+String.fromCharCode(176));
                        $("#unit").text('C');
						$("#desc").text(data.weather[0].main);
    					document.getElementById('icon').setAttribute('src',data.weather[0].icon);
    					document.getElementById('background').setAttribute('src',images[data.weather[0].main]);			
    				}
		}
	);
}